import React, { useState } from 'react';
import { useSelector, useDispatch } from "react-redux";
import { RootState } from "../State";
import { msgList } from "../State/type";
import io from 'socket.io-client';
import { bindActionCreators } from 'redux';
import { actionCreators } from '../State';
import "./CSS/AffMsg.css"
import "./CSS/All.css"

const socket = io('http://localhost:4000');

interface msgToSend {
    sender: string;
    recipient: string;
    text: string;
  }

// function msgBubble(props) {
//     return (
//         <div id="bulle">
//             <p>{text}</p>
//         </div>
//     )
    // if (sender)
    // {
    //     return (
    //         <div id="bulle">
    //             <p>{text}</p>
    //         </div>
    //     )
    // }
// }

// function UserGreeting() {
//     return <h1>Bienvenue !</h1>;
//   }

//   function GuestGreeting() {
//     return <h1>Veuillez vous inscrire.</h1>;
//   }

//   function Greeting(props: { isLoggedIn: any; }) {
//     if (props.isLoggedIn) {
//       return <UserGreeting />;
//     }
//     return <GuestGreeting />;
//   }


function AffMsg() {
    const msgList = useSelector((state: RootState) => state.msg)
    const logData = useSelector((state: RootState) => state.log)
    const dispatch = useDispatch();

    const { msgAdd, setUsername } = bindActionCreators(actionCreators, dispatch);

    const [id, setId] = useState('');
    const [text, setText] = useState('Wsh la team');
    const [recipient, setRecipient] = useState('');

    socket.removeAllListeners();
    socket.on('connect', function() {
        console.log('Connected');
        socket.emit('msgConnection');
    });
    socket.on('disconnect', function() {
        console.log('Disconnected');
    });
    socket.on('msgToClient', function(data) {
        if (data === 505)
        console.log('msgToServer', 'Msg bien reçu : 505');
        else
        console.log('msgToServer', data);
    });

    socket.on('ID', function(data) {
        console.log('ID received :', data);
        setId(data);
    });

    socket.on('msgInputToOtherClient', function(msgToSend) {
        console.log(msgToSend.sender, 'said: ', msgToSend.text);
        let newmsg: msgList = {
            sender: msgToSend.sender,
            text: msgToSend.text
        }
        msgAdd(newmsg);
    })

    function validateInput() {
        return id.length > 0 && text.length > 0 && recipient.length > 0;
    }

    function sendMessage() {
        if (validateInput()) {
        const message: msgToSend = {
            sender: id,
            recipient: recipient,
            text: text,
        };
        // const msgToList: msgList = {
        //     sender
        // }

        socket.emit('msgToOtherClient', message);
        msgAdd
        setText('');
        }
    }

    const newmsg: msgList = {
        text: "ok1",
        sender: "ok2"
    }

  return (
    <>
        <div className="main_container" id="main_AffMsg">
            <div id="affichage">
                <p>{msgList.count}</p>
                {msgList.msg.map((msg, index) => (
                    <div key={index} style={{display:"flex", justifyContent:"space-between"}}>
                        <div id="bulle">
                            <p>{text}</p>
                        </div>
                    </div>
                ))}
            </div>
            <div id="send">
                <input
                    value={recipient}
                    onChange={e => setRecipient(e.target.value)}
                    placeholder="Recipient..."
                />
                <input
                    value={text}
                    onChange={e => setText(e.target.value)}
                    placeholder="Enter message..."
                />
                <button type="button" onClick={() => sendMessage()}>
                    Send
                </button>
                <button onClick={() => msgAdd(newmsg)}>AddMsg Test</button>
                <button onClick={() => socket.emit('setUsername', "weeesssh")}>setUsername</button>
            </div>
        </div>
    </>
  );
}

export default AffMsg;
