import { Dispatch } from "redux"
import { action } from "typesafe-actions";
import { ActionType, clientListActionType, ListActionType, LogActionType } from "../Action-Types"
import { Action, clientListAction, listAction, logAction } from "../Actions"
import { Client, msg, msgList } from "../type"

